// ###Exercise 1 name: Orovwovo E. Oghale Matric no: CMP2307601#include <stdio.h>int main() {    // Declare and initialize variables    int userInput;    // Prompt user for input    printf("Enter a number: ");    // Read user input    scanf("%d", &userInput);    // Check if the number is divisible by 2    if (userInput % 2 == 0) {        // Print if the number is even        printf("%d is even\n", userInput);    } else {        // Print if the number is odd        printf("%d is odd\n", userInput);    }    return 0;}
